sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
  var allProps = {};
  allProps.ciBaseProperties = api.data.propertiesController.ciBaseProperties;
  allProps.ciExtendedProperties = api.data.propertiesController.ciExtendedProperties;
  allProps.ciConnectionProperties = api.data.propertiesController.ciConnectionProperties;
  allProps.hideCiProps = api.data.propertiesController.hideCiProps;
  allProps.hideConnectionProps = api.data.propertiesController.hideConnectionProps;
  allProps.showEmptyState = api.data.propertiesController.showEmptyState;
  var json = JSON.stringify(allProps, undefined, 4);
	return json;
}
